var btn=document.querySelector(".js-modal-btn"),modal=document.querySelector(".modal");function showModal(){modal.classList.remove("hidden"),modal.classList.add("open"),document.body.style.overflow="hidden",modal.classList.contains("open")&&window.addEventListener("click",function(o){o.target===modal&&(document.body.style.overflow="visible",modal.classList.add("hidden"),modal.classList.remove("open"))})}btn.addEventListener("click",showModal);